import React, { useState } from 'react';
import axios from 'axios';
import './ImageUploader.css';

const ImageUploader = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [features, setFeatures] = useState([]);
  const [topImages, setTopImages] = useState([]); // New state to hold the top 30 similar images

  const onFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setErrorMessage('');
  };

  const onUpload = async () => {
    if (!selectedFile) {
      setErrorMessage('Please select an image to upload');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedFile);

    setIsLoading(true);

    try {
      // Upload the image to the backend for feature extraction
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Log the features and top images returned from the backend
      console.log('Top 30 Similar Images:', response.data.topImages);

      // Set the top 30 similar images in state to display
      setTopImages(response.data.topImages);
    } catch (error) {
      console.error('Error uploading image:', error);
      setErrorMessage('An error occurred while uploading the image.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Feature Extraction from Image</h1>
      <input
        type="file"
        id="file-upload"
        className="file-upload"
        onChange={onFileChange}
      />
      <label htmlFor="file-upload" className="custom-file-upload">
        Upload file
      </label>
      {selectedFile && (
        <div className="uploaded-image">
          <h2>Uploaded Image:</h2>
          <img
            src={URL.createObjectURL(selectedFile)}
            alt="Uploaded"
            className="thumbnail"
          />
        </div>
      )}
      <button onClick={onUpload} disabled={isLoading}>
        {isLoading ? 'Uploading...' : 'Extract Features'}
      </button>

      {errorMessage && <p className="error">{errorMessage}</p>}

      {topImages.length > 0 && (
        <div className="results">
          <h2>Top 30 Similar Images:</h2>
          <ul>
            {topImages.map((img, index) => (
              <li key={index}>
                <strong>{img.imageName}</strong> - Similarity: {img.similarity.toFixed(4)}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ImageUploader;
