import React, { useState } from 'react';
import axios from 'axios';
import './ImageUploader.css';

const ImageUploader = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [similarImages, setSimilarImages] = useState([]);

  const onFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setErrorMessage('');
  };

  const onUpload = async () => {
    if (!selectedFile) {
      setErrorMessage('Please select an image to search');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedFile);

    setIsLoading(true);

    try {
      // Upload the selected image to the backend
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('Similar images:', response.data.similarImages);

      // Update the state to display the names of similar images
      setSimilarImages(response.data.similarImages);
    } catch (error) {
      console.error('Error uploading image:', error);
      // Display the specific error message from the backend
      setErrorMessage(error.response?.data || 'An error occurred while uploading the image.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Similar Images Search</h1>
      <input
        type="file"
        id="file-upload"
        className="file-upload"
        onChange={onFileChange}
      />
      <label htmlFor="file-upload" className="custom-file-upload">
        Upload file
      </label>
      {selectedFile && (
        <div className="uploaded-image">
          <h2>Uploaded Image:</h2>
          <img
            src={URL.createObjectURL(selectedFile)} // Display the uploaded image
            alt="Uploaded"
            className="thumbnail"
          />
        </div>
      )}
      <button onClick={onUpload} disabled={isLoading}>
        {isLoading ? 'Uploading...' : 'Search images'}
      </button>

      {errorMessage && <p className="error">{errorMessage}</p>}

      {similarImages.length > 0 && (
        <div className="results">
          <h2>Similar Images:</h2>
          <ul>
            {similarImages.map((image, index) => (
              <li key={index}>{image}</li> // Display names of similar images
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ImageUploader;
