import React, { useState } from 'react';
import axios from 'axios';
import './ImageUploader.css';

const ImageUploader = () => {
    const [similarImages, setSimilarImages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [isSearching, setIsSearching] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const folderNames = ['0', '1', '2']; // Noms des dossiers, à ajuster si nécessaire

    const onFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
        setSimilarImages([]); // Réinitialiser les images similaires à chaque nouvelle sélection
        setErrorMessage('');
    };

    const onUpload = async () => {
        if (!selectedFile) {
            setErrorMessage('Please select an image to search');
            return;
        }

        const formData = new FormData();
        formData.append('image', selectedFile);

        setIsLoading(true);
        setIsSearching(false);

        try {
            // Upload de l'image sélectionnée
            const response = await axios.post('http://localhost:5001/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            setIsSearching(true); // Indiquer que la recherche commence

            // Traitement des images similaires retournées
            const fetchedImages = response.data.similarImages.map((img) => ({
                imageName: img.imageName,
                distance: img.distance,
                thumbnailUrl: getThumbnailUrl(img.imageName),
            }));

            setSimilarImages(fetchedImages);
        } catch (error) {
            console.error('Error uploading image:', error);
            setErrorMessage('An error occurred while uploading the image.');
        } finally {
            setIsLoading(false); // Fin du chargement
        }
    };

    const getThumbnailUrl = (imageName) => {
        // Itérer à travers chaque dossier pour trouver la miniature
        for (const folderName of folderNames) {
            const imageUrl = `http://localhost:5001/thumbnails/${folderName}/${imageName}`;
            return imageUrl; // Retourner la première URL de miniature trouvée
        }
        return ''; // Retourner une chaîne vide si aucune miniature n'est trouvée
    };

    return (
        <div className="container">
            <h1>Similar Images Search</h1>
            <input
                type="file"
                id="file-upload"
                className="file-upload"
                onChange={onFileChange}
            />


            <label htmlFor="file-upload" className="custom-file-upload">
                Upload file
            </label>

            <button onClick={onUpload} disabled={isLoading}>
                {isLoading ? 'Uploading...' : 'Search images'}
            </button>

            {errorMessage && <p className="error">{errorMessage}</p>}

            {selectedFile && (
                <div className="uploaded-image">
                    <h2>Uploaded Image:</h2>
                    <img
                        src={URL.createObjectURL(selectedFile)} // Afficher l'image téléchargée
                        alt="Uploaded"
                        className="thumbnail"
                    />
                </div>
            )}

            {isSearching && <p className="loading">Searching for similar images...</p>} {/* Indicateur de recherche */}

            {similarImages.length > 0 && (
                <div className="results">
                    <h2>Similar Images:</h2>
                    <div className="image-grid">
                        {similarImages.map((image) => (
                            <div key={image.imageName} className="image-item">
                                <img
                                    src={image.thumbnailUrl}
                                    alt={`Similar to ${selectedFile?.name}`}
                                    className="thumbnail"
                                />
                                <p>Distance: {image.distance.toFixed(2)}</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default ImageUploader;
