import React, { useState } from 'react';
import axios from 'axios';

const ImageUploader = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [features, setFeatures] = useState([]);
    const [featureSize, setFeatureSize] = useState(0);
    const [similarImages, setSimilarImages] = useState([]); // State to hold similar images and distances
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const onFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
        setError(''); // Reset error message on file change
    };

    const onFileUpload = async () => {
        if (!selectedFile) {
            setError('Please select an image file.');
            return;
        }

        const formData = new FormData();
        formData.append('image', selectedFile);

        setLoading(true);
        try {
            const response = await axios.post('http://localhost:5001/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            // Set features and their size
            setFeatures(response.data.features);
            setFeatureSize(response.data.features.length);
            setSimilarImages(response.data.similarImages || []); // Store the similar images data
            setLoading(false);
        } catch (err) {
            console.error('Error uploading file:', err);
            setError('Error uploading file, please try again.');
            setLoading(false);
        }
    };

    return (
        <div>
            <h2>Upload Image for Feature Extraction</h2>
            <input type="file" accept="image/*" onChange={onFileChange} />
            <button onClick={onFileUpload} disabled={loading}>
                {loading ? 'Uploading...' : 'Upload'}
            </button>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {features.length > 0 && (
                <div>
                    <h3>Extracted Features:</h3>
                    <p>Size of features array: {featureSize}</p>
                    <pre>{JSON.stringify(features, null, 2)}</pre> {/* Display features as a formatted string */}
                </div>
            )}

            {similarImages.length > 0 && (
                <div>
                    <h3>Similar Images:</h3>
                    <ul>
                        {similarImages.map((item, index) => (
                            <li key={index}>
                                {item.imageName} - Distance: {item.distance.toFixed(2)} {/* Display image name and distance */}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default ImageUploader;
