import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ImageUpload = () => {
    const [file, setFile] = useState(null);
    const [closestImages, setClosestImages] = useState([]);

    const handleFileSelect = (e) => {
        setFile(e.target.files[0]);
    };

    const handleUpload = async () => {
        const formData = new FormData();
        formData.append('image', file);

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setClosestImages(response.data); // Store closest images
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    return (
        <div>
            <h2>Upload an Image</h2>
            <input type="file" onChange={handleFileSelect} />
            <button onClick={handleUpload}>Upload Image</button>

            <h2>Closest Images</h2>
            <div>
                {closestImages.map((img, index) => (
                    <div key={index}>
                        <img src={img.url} alt={img.name} width="100" />
                        <p>Name: {img.name}</p>
                        <p>Distance: {img.distance.toFixed(4)}</p> {/* Optional: format the distance */}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ImageUpload;
