import React, { useState } from 'react';
import axios from 'axios';
import './ImageUploader.css';

const ImageUploader = () => {
    const [similarImages, setSimilarImages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [imagePath, setImagePath] = useState(''); // État pour le chemin de l'image
    const [isLoading, setIsLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const onFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
        setErrorMessage(''); // Réinitialiser le message d'erreur à chaque nouvelle sélection
    };

    const onPathChange = (event) => {
        setImagePath(event.target.value); // Mettre à jour l'état avec le chemin de l'image
        setErrorMessage(''); // Réinitialiser le message d'erreur
    };

    const onUpload = async () => {
        if (!selectedFile && !imagePath) {
            setErrorMessage('Please select an image or enter a path to search');
            return;
        }

        const formData = new FormData();
        if (selectedFile) {
            formData.append('image', selectedFile);
        }

        setIsLoading(true);

        try {
            const response = await axios.post('http://localhost:5001/upload', {
                imagePath: imagePath || undefined, // Passer le chemin d'image si disponible
                formData: selectedFile ? formData : undefined // Passer formData si un fichier est sélectionné
            });
            const fetchedImages = response.data.similarImages.map((img) => ({
                imageName: img.imageName,
                distance: img.distance,
                thumbnailUrl: getThumbnailUrl(img.imageName),
            }));
            setSimilarImages(fetchedImages);
        } catch (error) {
            console.error('Error uploading image:', error);
            setErrorMessage('An error occurred while uploading the image.');
        } finally {
            setIsLoading(false);
        }
    };

    const getThumbnailUrl = (imageName) => {
        const folderName = 'folder1'; // Remplacez par la logique appropriée pour déterminer le dossier
        return `http://localhost:5001/thumbnails/${folderName}/${imageName}`;
    };

    return (
        <div className="container">
            <h1>Similar Images Search</h1>
            <input
                type="file"
                id="file-upload"
                className="file-upload"
                onChange={onFileChange}
            />
            <label htmlFor="file-upload" className="custom-file-upload">
                Upload file
            </label>

            {/* Champ d'entrée pour le chemin de l'image */}
            <input
                type="text"
                placeholder="Enter image path"
                value={imagePath}
                onChange={onPathChange}
                className="image-path-input"
            />

            <button onClick={onUpload} disabled={isLoading}>
                {isLoading ? 'Uploading...' : 'Search images'}
            </button>

            {errorMessage && <p className="error">{errorMessage}</p>}

            {similarImages.length > 0 && (
                <div className="results">
                    <h2>Similar Images:</h2>
                    <div className="image-grid">
                        {similarImages.map((image) => (
                            <div key={image.imageName} className="image-item">
                                <img
                                    src={image.thumbnailUrl}
                                    alt={`Similar to ${selectedFile?.name}`}
                                    className="thumbnail"
                                />
                                <p>Distance: {image.distance.toFixed(2)}</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default ImageUploader;
