// src/ImageUploader.js

import React, { useState } from 'react';
import axios from 'axios';
import './ImageUploader.css'; // Importer le CSS

const ImageUploader = () => {
    const [image, setImage] = useState(null);
    const [similarImages, setSimilarImages] = useState([]);

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    const handleUpload = async () => {
        const formData = new FormData();
        formData.append('image', image);

        try {
            const response = await axios.post('http://localhost:3001/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setSimilarImages(response.data.similarImages);
        } catch (error) {
            console.error('Erreur lors du téléchargement de l\'image', error);
        }
    };

    return (
        <div className="container">
            <h1>Téléchargez une Image</h1>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            <button onClick={handleUpload} disabled={!image}>Télécharger</button>
            {similarImages.length > 0 && (
                <div className="results">
                    <h2>Images Similaires</h2>
                    <ul>
                        {similarImages.map((img) => (
                            <li key={img.id}>
                                <img src={img.imagePath} alt="Similar" />
                                <p>Distance: {img.distance.toFixed(2)}</p>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default ImageUploader;
