import React, { useState } from 'react';
import axios from 'axios';
import './ImageUploader.css';

const ImageUploader = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [features, setFeatures] = useState([]);
  const [similarImages, setSimilarImages] = useState([]);

  const onFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setErrorMessage('');
  };

  const onUpload = async () => {
    if (!selectedFile) {
      setErrorMessage('Please select an image to upload');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedFile);

    setIsLoading(true);

    try {
      // Upload the image to the backend for feature extraction
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Log the features returned from the backend
      console.log('Extracted features:', response.data.features);

      // Set the extracted features in state to display
      setFeatures(response.data.features);

      // Now search for similar images based on the extracted features
      const searchResponse = await axios.post('http://localhost:5000/search', {
        queryFeatures: response.data.features,
      });

      // Set the similar images
      setSimilarImages(searchResponse.data);
    } catch (error) {
      console.error('Error uploading image:', error);
      setErrorMessage('An error occurred while uploading the image.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Feature Extraction and Image Similarity</h1>
      <input
        type="file"
        id="file-upload"
        className="file-upload"
        onChange={onFileChange}
      />
      <label htmlFor="file-upload" className="custom-file-upload">
        Upload file
      </label>
      {selectedFile && (
        <div className="uploaded-image">
          <h2>Uploaded Image:</h2>
          <img
            src={URL.createObjectURL(selectedFile)}
            alt="Uploaded"
            className="thumbnail"
          />
        </div>
      )}
      <button onClick={onUpload} disabled={isLoading}>
        {isLoading ? 'Uploading...' : 'Extract Features'}
      </button>

      {errorMessage && <p className="error">{errorMessage}</p>}

      {features.length > 0 && (
        <div className="results">
          <h2>Extracted Features:</h2>
          <pre>{JSON.stringify(features, null, 2)}</pre>
        </div>
      )}

      {similarImages.length > 0 && (
        <div className="similar-images">
          <h2>Similar Images:</h2>
          <ul>
            {similarImages.map((image, index) => (
              <li key={index}>{image.imageName} (Similarity: {image.similarity})</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ImageUploader;
