import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [imageFile, setImageFile] = useState(null);
  const [similarImages, setSimilarImages] = useState([]);

  const handleImageChange = (event) => {
    setImageFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!imageFile) {
      alert('Veuillez sélectionner une image à télécharger !');
      return;
    }

    const formData = new FormData();
    formData.append('image', imageFile);

    try {
      const response = await axios.post('http://localhost:3001/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setSimilarImages(response.data);
    } catch (error) {
      console.error('Erreur lors du téléchargement de l\'image :', error);
    }
  };

  return (
    <div>
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleUpload}>Télécharger l'image</button>

      {similarImages.length > 0 && <h2>Images similaires :</h2>}
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {similarImages.map((img, index) => (
          <div key={index} style={{ margin: '10px' }}>
            <img src={img.image_url} alt={img.image_name} style={{ width: '100px', height: '100px' }} />
            <p>{img.image_name}</p>
            <p>Distance : {img.distance}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
