import React, { useState } from 'react';
import axios from 'axios';

const ImageUploader = () => {
    const [similarImages, setSimilarImages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);

    const onFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const onUpload = async () => {
        if (!selectedFile) {
            alert('Please select an image to upload');
            return;
        }

        const formData = new FormData();
        formData.append('image', selectedFile);

        try {
            const response = await axios.post('http://localhost:5001/upload', formData);
            const fetchedImages = response.data.similarImages.map((img) => ({
                imageName: img.imageName,
                distance: img.distance,
                thumbnailUrl: getThumbnailUrl(img.imageName), // Assuming you have a function to generate the thumbnail URL
            }));
            setSimilarImages(fetchedImages);
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    const getThumbnailUrl = (imageName) => {
        return `path/to/thumbnails/${imageName}`; // Remplacez par votre chemin d'accès aux vignettes
    };

    return (
        <div>
            <input type="file" onChange={onFileChange} />
            <button onClick={onUpload}>Upload Image</button>
            {similarImages.length > 0 && (
                <div>
                    <h2>Images similaires :</h2>
                    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                        {similarImages.map((image) => (
                            <div key={image.imageName} style={{ margin: '10px' }}>
                                <img
                                    src={image.thumbnailUrl}
                                    alt={image.imageName}
                                    style={{ width: '100px', height: '100px' }} // Ajustez la taille selon vos besoins
                                />
                                <p>Distance: {image.distance.toFixed(2)}</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default ImageUploader;
