import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [image, setImage] = useState(null);
  const [similarImages, setSimilarImages] = useState([]);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!image) {
      alert('Veuillez sélectionner une image');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);

    try {
      const response = await axios.post('http://localhost:3001/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      // Mettre à jour l'état avec les images similaires
      setSimilarImages(response.data.similarImages);
    } catch (error) {
      console.error('Erreur lors du téléchargement de l\'image:', error);
    }
  };

  return (
    <div className="App">
      <h1>Recherche d'images similaires</h1>
      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        <button type="submit">Télécharger et rechercher</button>
      </form>

      {similarImages.length > 0 && (
        <div>
          <h2>Images similaires :</h2>
          <div style={{ display: 'flex', gap: '10px' }}>
            {similarImages.map((img) => (
              <div key={img.id}>
                <img src={`http://localhost:3001/${img.imagePath}`} alt="Image similaire" width="150" />
                <p>Distance : {img.distance.toFixed(2)}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
