import React, { useState } from 'react';
import axios from 'axios';

const ImageUpload = () => {
    const [closestImages, setClosestImages] = useState([]);
    const [imageName, setImageName] = useState(''); // To store the image name for the search

    const handleSearch = async () => {
        try {
            // Replace `imageName` with the actual name or path of the image you want to search for
            const response = await axios.get(`http://localhost:5000/images?name=${imageName}`);
            setClosestImages(response.data); // Store closest images
        } catch (error) {
            console.error('Error fetching images:', error);
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Enter image name or path"
                value={imageName}
                onChange={(e) => setImageName(e.target.value)}
            />
            <button onClick={handleSearch}>Search Images</button>
            <div>
                {closestImages.map((img, index) => (
                    <div key={index}>
                        {/* Assuming 'img.url' contains the image URL */}
                        <img src={img.url} alt={img.name} style={{ width: '100px', height: '100px' }} />
                        <p>{img.name}</p>
                        <p>Distance: {img.distance}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ImageUpload;
