// ImageUpload.js
import React, { useState } from 'react';
import axios from 'axios';

const ImageUpload = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [results, setResults] = useState([]);

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('image', selectedFile);

        try {
            const response = await axios.post('http://localhost:5000/api/search', formData);
            setResults(response.data);
        } catch (error) {
            console.error('Error uploading image:', error.response.data);
            alert(`Error uploading image: ${error.response.data.error || error.message}`);
        }
    };

    return (
        <div>
            <h1>Image Upload and Search</h1>
            <form onSubmit={handleSubmit}>
                <input type="file" accept="image/*" onChange={handleFileChange} required />
                <button type="submit">Upload Image</button>
            </form>
            <h2>Results</h2>
            {results.length > 0 && (
                <ul>
                    {results.map((result, index) => (
                        <li key={index}>
                            {result.image_name}: Distance {result.distance.toFixed(4)}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ImageUpload;
