import React, { useState } from 'react';
import axios from 'axios';

const ImageUpload = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [results, setResults] = useState([]);

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('image', selectedFile);

        try {
            const response = await axios.post('http://localhost:5000/api/search', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setResults(response.data); // Enregistrez les résultats pour les afficher
        } catch (error) {
            console.error('Erreur lors de l\'upload de l\'image:', error);
        }
    };

    return (
        <div>
            <h1>Uploader une Image</h1>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange} />
                <button type="submit">Chercher des Images Similaires</button>
            </form>
            <div>
                {results.map((result, index) => (
                    <div key={index}>
                        <img src={result._source.path} alt={`Image similaire ${index}`} />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ImageUpload;
