import React, { useState } from 'react';
import axios from 'axios';

const ImageUpload = () => {
    const [file, setFile] = useState(null);
    const [results, setResults] = useState([]);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('image', file);

        try {
            const response = await axios.post('http://localhost:5000/api/search', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setResults(response.data); // Mettre à jour les résultats avec les images similaires
        } catch (error) {
            console.error('Erreur lors du téléchargement de l\'image :', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange} accept="image/*" required />
                <button type="submit">Télécharger et Rechercher</button>
            </form>
            {results.length > 0 && (
                <div>
                    <h2>Résultats :</h2>
                    <ul>
                        {results.map((result, index) => (
                            <li key={index}>
                                <p>{result._source.image_name}</p> {/* Afficher le nom de l'image ou d'autres détails */}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default ImageUpload;
