import React, { useState } from 'react';
import axios from 'axios';

const ImageUpload = () => {
    const [imageFile, setImageFile] = useState(null);
    const [similarImages, setSimilarImages] = useState([]);

    const handleImageChange = (event) => {
        setImageFile(event.target.files[0]); // Set the uploaded image file
    };

    const handleUpload = async () => {
        if (!imageFile) {
            alert('Please select an image to upload!');
            return;
        }

        const formData = new FormData();
        formData.append('image', imageFile); // Append the image file to FormData

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setSimilarImages(response.data); // Set the similar images returned from the server
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    return (
        <div>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            <button onClick={handleUpload}>Upload Image</button>

            <div>
                {similarImages.length > 0 && (
                    <h2>Similar Images:</h2>
                )}
                <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    {similarImages.map((img, index) => (
                        <div key={index} style={{ margin: '10px' }}>
                            <img src={img.image_url} alt={img.image_name} style={{ width: '100px', height: '100px' }} />
                            <p>{img.image_name}</p>
                            <p>Distance: {img.distance}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default ImageUpload;
