import React, { useState } from 'react';
import axios from 'axios';

function ImageUploader() {
  const [image, setImage] = useState(null);
  const [topImages, setTopImages] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!image) {
      alert("Please select an image first!");
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('image', image);

    try {
      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Handle the response from the backend
      console.log(response.data);
      setTopImages(response.data.topImages);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
    setLoading(false);
  };

  return (
    <div>
      <h1>Upload Image</h1>
      <input type="file" onChange={handleImageChange} />
      <button onClick={handleUpload} disabled={loading}>
        {loading ? 'Uploading...' : 'Upload'}
      </button>

      <div>
        <h2>Top 30 Similar Images</h2>
        <div>
          {topImages.length === 0 && <p>No results to display</p>}
          {topImages.map((image, index) => (
            <div key={index} style={{ margin: '10px 0' }}>
              <img src={image.imagePath} alt={image.imageName} style={{ width: '100px', height: '100px' }} />
              <p>Similarity: {image.similarity}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ImageUploader;
